import pandas_datareader.data as pdrimport numpy as npimport datetime# Download historical data for reuired stocksticker = 'NIO'ohlcv = pdr.get_data_yahoo(ticker, datetime.date.today() - datetime.timedelta(1025), datetime.date.today())n = 14def volatility(DF):    df = DF.copy()    df['daily_ret'] = DF['Adj Close'].pct_change()    vol = df['daily_ret'].std() * np.sqrt(252)    return volvol = volatility(ohlcv)